import React from 'react';
import { useState } from 'react'

export default function DistanceToTime() {
    const [distance, setDistance] = React.useState({ walking: 0, driving: 0, flying: 0});



    const updateValue = e => setDistance ({
        value: e.target.value,
        walking: (+e.target.value *60).toFixed(0),
        driving: (+e.target.value *60).toFixed(0),
        flying: (+e.target.value *60).toFixed(0)
    })

    return (
        <div>
               <form className="container">
               <div id="box1">
        <h1>Add Distance</h1>
        <input
            type = "number"
            value = {distance.value}
            onChange = {updateValue} >
          </input>
      </div>
                    Enter your Distance:<br/>
                    <input type="number" value = {distance.walking} 
            onChange = {updateValue}></input><br/>
                    Distance when Walking<br/>
                    <input type="number" 
                    value = {distance.driving} 
                    onChange = {updateValue}></input><br/>
                    Distance When Driving<br/>
                    <input type="number" 
                    value = {distance.flying} 
                    onChange = {updateValue}></input><br/>

                </form>
        </div>
    )
}
