    // var seconds = 0;


    return{
        'days': days,
        'hours': hours,
        'minutes': minutes,
        'seconds': seconds,
    }


}



componentWillMount = () => {
    this.setState = {
        timeRemaining: this.getTimeRemaining()
    }
}

