import React from 'react';
import { useState } from 'react'

export default function DistanceToTime() {
    const [distance, setDistance] = useState({ value:"0", walking: 0, driving: 0, flying: 0 });

    var averageSpeedWhenWalking = 3.6;
    var averageSpeedWhenDriving = 50;
    var averageSpeedWhenFlying = 800;

    const updateDstanceValue = e => setDistance({
        value: e.target.value,
        walking: ((+e.target.value * 60) / averageSpeedWhenWalking).toFixed(2),
        driving: ((+e.target.value * 60) / averageSpeedWhenDriving).toFixed(2),
        flying: ((+e.target.value * 60) / averageSpeedWhenFlying).toFixed(2),

    })

    const convertTime = (n) => {
        var givenNumber = n;
        var convertedHours = (givenNumber / 60);
        var minutes = Math.floor(convertedHours);
        var minutesToBeReturned = (convertedHours - minutes) * 60;
        var convertedMinutes = Math.round(minutesToBeReturned);
        return givenNumber + " minutes = " + minutes + " :hours and " + minutesToBeReturned + " :minutes";
    }





    return (
        <div>
            <form className="container">
                <div>
                    <h1>Enter The Distance</h1>
                    <input
                        type="number"
                        value={distance.value}
                        onChange={(e) =>updateDstanceValue(e)}
                    >
                    </input>
                </div>
            </form>

            <p>{distance.walking}</p>
            <p>{distance.driving}</p>
            <p>{distance.flying}</p>



        </div>
    )
}
